import React, { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import { useCart } from "../context/CartContext";
import { FaStar, FaShoppingCart, FaCheck, FaTruck, FaUndo } from "react-icons/fa";
import Loading, { LoadingSkeleton } from "../components/Loading";
import "./ProductDetails.css";

export default function ProductDetailsPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();

  const [product, setProduct] = useState(null);
  const [related, setRelated] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [reviewsLoading, setReviewsLoading] = useState(false);
  const [quantity, setQuantity] = useState(1);
  const [newReview, setNewReview] = useState({ rating: 5, comment: "" });
  const [submitingReview, setSubmitingReview] = useState(false);
  const [selectedImage, setSelectedImage] = useState(0);
  const [averageRating, setAverageRating] = useState(0);
  const [ratingDistribution, setRatingDistribution] = useState({});

  // Fetch product details
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        setLoading(true);
        const res = await fetch(`http://localhost:5000/api/products/${id}`);
        const response = await res.json();
        const data = response.data || response;
        setProduct(data);
        setSelectedImage(0);

        // Fetch related products by category
        const relRes = await fetch(
          `http://localhost:5000/api/products?category=${data.category}&limit=6`
        );
        const relResponse = await relRes.json();
        const relData = relResponse.data || relResponse;
        setRelated(Array.isArray(relData) ? relData.filter((p) => p._id !== data._id).slice(0, 6) : []);

        // Fetch reviews
        fetchReviews(id);
      } catch (err) {
        console.error("Error fetching product:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchProduct();
  }, [id]);

  // Fetch reviews
  const fetchReviews = async (productId) => {
    try {
      setReviewsLoading(true);
      const res = await fetch(`http://localhost:5000/api/products/${productId}/reviews`);
      const response = await res.json();
      const data = response.data || response;
      const reviewsArray = Array.isArray(data) ? data : [];
      setReviews(reviewsArray);
      calculateRatings(reviewsArray);
    } catch (err) {
      console.error("Error fetching reviews:", err);
    } finally {
      setReviewsLoading(false);
    }
  };

  // Calculate average rating and distribution
  const calculateRatings = (reviewsList) => {
    if (reviewsList.length === 0) {
      setAverageRating(0);
      setRatingDistribution({});
      return;
    }

    const distribution = { 5: 0, 4: 0, 3: 0, 2: 0, 1: 0 };
    let totalRating = 0;

    reviewsList.forEach((review) => {
      totalRating += review.rating;
      distribution[review.rating]++;
    });

    setAverageRating((totalRating / reviewsList.length).toFixed(1));
    setRatingDistribution(distribution);
  };

  // Handle review submission
  const handleReviewSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please login to submit a review");
      navigate("/login");
      return;
    }

    try {
      setSubmitingReview(true);
      const res = await fetch(`http://localhost:5000/api/products/${id}/reviews`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newReview),
      });

      if (res.ok) {
        alert("✅ Review submitted successfully!");
        setNewReview({ rating: 5, comment: "" });
        fetchReviews(id);
      } else {
        const err = await res.json();
        alert("❌ " + (err.message || "Failed to submit review"));
      }
    } catch (err) {
      console.error(err);
      alert("❌ Error submitting review");
    } finally {
      setSubmitingReview(false);
    }
  };

  // Handle add to cart
  const handleAddToCart = () => {
    for (let i = 0; i < quantity; i++) {
      addToCart(product);
    }
    alert(`✅ Added ${quantity} item(s) to cart!`);
    setQuantity(1);
  };

  if (loading) return <Loading fullScreen message="Loading product details..." />;
  if (!product) return <div className="error-message">Product not found</div>;

  return (
    <div className="pdp-container">
      {/* BREADCRUMB */}
      <div className="breadcrumb">
        <Link to="/">Home</Link>
        <span>/</span>
        <Link to="/">Products</Link>
        <span>/</span>
        <span>{product.name}</span>
      </div>

      {/* MAIN PRODUCT SECTION */}
      <div className="pdp-main">
        {/* IMAGE GALLERY */}
        <div className="image-gallery">
          <div className="main-image">
            <img 
              src={product.image || "https://via.placeholder.com/400"} 
              alt={product.name}
            />
            {product.countInStock === 0 && (
              <div className="out-of-stock-overlay">OUT OF STOCK</div>
            )}
          </div>
          <div className="image-thumbnails">
            <img 
              src={product.image || "https://via.placeholder.com/400"} 
              alt="thumbnail"
              className="thumbnail active"
              onClick={() => setSelectedImage(0)}
            />
          </div>
        </div>

        {/* PRODUCT INFO */}
        <div className="product-info-section">
          {/* TITLE & RATING */}
          <div className="product-header">
            <h1 className="product-title">{product.name}</h1>
            <div className="rating-summary">
              <div className="stars">
                {[...Array(5)].map((_, i) => (
                  <FaStar
                    key={i}
                    className={i < Math.round(averageRating) ? "star filled" : "star"}
                  />
                ))}
              </div>
              <span className="rating-value">{averageRating}</span>
              <span className="review-count">({reviews.length} reviews)</span>
            </div>
          </div>

          {/* PRICE & STOCK */}
          <div className="price-section">
            <div className="price-display">
              <span className="currency">R</span>
              <span className="amount">{product.price?.toFixed(2) || "0.00"}</span>
            </div>
            <div className={`stock-status ${product.countInStock > 0 ? "in-stock" : "out-of-stock"}`}>
              {product.countInStock > 0 ? (
                <>
                  <FaCheck /> In Stock ({product.countInStock} available)
                </>
              ) : (
                <>
                  ❌ Out of Stock
                </>
              )}
            </div>
          </div>

          {/* DESCRIPTION */}
          <div className="description-section">
            <h3>Description</h3>
            <p>{product.description}</p>
            {product.brand && <p><strong>Brand:</strong> {product.brand}</p>}
            {product.category && <p><strong>Category:</strong> {product.category}</p>}
          </div>

          {/* QUANTITY & ADD TO CART */}
          <div className="purchase-section">
            <div className="quantity-selector">
              <label htmlFor="quantity">Quantity:</label>
              <div className="quantity-input">
                <button 
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  disabled={quantity <= 1}
                >
                  −
                </button>
                <input 
                  type="number" 
                  value={quantity} 
                  onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
                  min="1"
                  max={product.countInStock}
                />
                <button 
                  onClick={() => setQuantity(Math.min(product.countInStock, quantity + 1))}
                  disabled={quantity >= product.countInStock}
                >
                  +
                </button>
              </div>
            </div>

            <button 
              className="add-to-cart-btn"
              onClick={handleAddToCart}
              disabled={product.countInStock === 0}
            >
              <FaShoppingCart /> Add to Cart
            </button>
          </div>

          {/* DELIVERY & RETURNS */}
          <div className="shipping-info">
            <div className="info-item">
              <FaTruck className="icon" />
              <div>
                <strong>Free Delivery</strong>
                <p>On orders over R500</p>
              </div>
            </div>
            <div className="info-item">
              <FaUndo className="icon" />
              <div>
                <strong>Easy Returns</strong>
                <p>30-day return policy</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* REVIEWS SECTION */}
      <div className="reviews-container">
        <div className="reviews-header">
          <h2>Customer Reviews & Ratings</h2>
        </div>

        <div className="reviews-content">
          {/* RATING SUMMARY */}
          <div className="rating-summary-box">
            <div className="average-rating">
              <div className="big-rating">{averageRating}</div>
              <div className="stars-large">
                {[...Array(5)].map((_, i) => (
                  <FaStar
                    key={i}
                    className={i < Math.round(averageRating) ? "star filled" : "star"}
                  />
                ))}
              </div>
              <p className="total-reviews">Based on {reviews.length} reviews</p>
            </div>

            {/* RATING DISTRIBUTION */}
            <div className="rating-distribution">
              {[5, 4, 3, 2, 1].map((rating) => (
                <div key={rating} className="rating-bar">
                  <span className="rating-label">{rating} ⭐</span>
                  <div className="bar-container">
                    <div 
                      className="bar-fill"
                      style={{
                        width: `${reviews.length > 0 ? (ratingDistribution[rating] / reviews.length) * 100 : 0}%`
                      }}
                    ></div>
                  </div>
                  <span className="count">{ratingDistribution[rating] || 0}</span>
                </div>
              ))}
            </div>
          </div>

          {/* REVIEWS LIST */}
          <div className="reviews-list">
            <h3>Customer Reviews</h3>

            {reviewsLoading ? (
              <LoadingSkeleton count={3} height="100px" />
            ) : reviews.length === 0 ? (
              <div className="no-reviews">
                <p>No reviews yet. Be the first to review this product!</p>
              </div>
            ) : (
              reviews.map((review) => (
                <div key={review._id} className="review-item">
                  <div className="review-header">
                    <div className="reviewer-info">
                      <strong className="reviewer-name">{review.name || "Anonymous"}</strong>
                      <span className="review-date">
                        {new Date(review.createdAt).toLocaleDateString()}
                      </span>
                    </div>
                    <div className="review-rating">
                      {[...Array(5)].map((_, i) => (
                        <FaStar
                          key={i}
                          className={i < review.rating ? "star filled" : "star"}
                        />
                      ))}
                    </div>
                  </div>
                  <p className="review-comment">{review.comment}</p>
                </div>
              ))
            )}
          </div>
        </div>

        {/* WRITE REVIEW FORM */}
        <div className="write-review-section">
          <h3>Write Your Review</h3>
          <form onSubmit={handleReviewSubmit} className="review-form">
            <div className="form-group">
              <label htmlFor="rating">Rating *</label>
              <div className="rating-input">
                {[1, 2, 3, 4, 5].map((star) => (
                  <button
                    key={star}
                    type="button"
                    className={`star-btn ${newReview.rating >= star ? "active" : ""}`}
                    onClick={() => setNewReview({ ...newReview, rating: star })}
                  >
                    <FaStar />
                  </button>
                ))}
              </div>
              <p className="rating-text">
                {newReview.rating > 0 && `You rated: ${newReview.rating} star${newReview.rating > 1 ? "s" : ""}`}
              </p>
            </div>

            <div className="form-group">
              <label htmlFor="comment">Your Review *</label>
              <textarea
                id="comment"
                placeholder="Share your experience with this product..."
                value={newReview.comment}
                onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
                required
                minLength="10"
                maxLength="500"
                rows="5"
              ></textarea>
              <p className="char-count">{newReview.comment.length}/500</p>
            </div>

            <button 
              type="submit" 
              className="submit-review-btn"
              disabled={submitingReview || !newReview.comment}
            >
              {submitingReview ? "Submitting..." : "Submit Review"}
            </button>
          </form>
        </div>
      </div>

      {/* RELATED PRODUCTS */}
      <div className="related-products-section">
        <h2>You Might Also Like</h2>
        <div className="related-products-grid">
          {related.length === 0 ? (
            <p>No related products found</p>
          ) : (
            related.map((relatedProduct) => (
              <Link 
                key={relatedProduct._id} 
                to={`/product/${relatedProduct._id}`}
                className="related-product-card"
              >
                <div className="related-product-image">
                  <img 
                    src={relatedProduct.image || "https://via.placeholder.com/200"} 
                    alt={relatedProduct.name}
                  />
                </div>
                <div className="related-product-info">
                  <h4>{relatedProduct.name}</h4>
                  <div className="related-product-rating">
                    {[...Array(5)].map((_, i) => (
                      <FaStar
                        key={i}
                        className={i < Math.round(relatedProduct.rating || 0) ? "star filled" : "star"}
                      />
                    ))}
                    <span>({relatedProduct.reviews?.length || 0})</span>
                  </div>
                  <p className="related-product-price">R{relatedProduct.price?.toFixed(2) || "0.00"}</p>
                </div>
              </Link>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
