import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { FaSearch, FaFilter, FaStar, FaShoppingCart, FaArrowRight, FaHeart } from "react-icons/fa";
import { useCart } from "../context/CartContext";
import WishlistButton from "../components/WishlistButton";
import Loading from "../components/Loading";
import "./Home.css";

export default function Home() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("");
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");
  const { addToCart } = useCart();


  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      if (search) params.append("search", search);
      if (category) params.append("category", category);
      if (minPrice) params.append("min", minPrice);
      if (maxPrice) params.append("max", maxPrice);

      const res = await fetch(`http://localhost:5000/api/products?${params}`);
      const response = await res.json();

      // FIX: Handle both API response formats
      if (response.data && Array.isArray(response.data)) {
        setProducts(response.data);
      } else if (Array.isArray(response)) {
        setProducts(response);
      } else {
        console.warn("Unexpected API response format", response);
        setProducts([]);
      }
    } catch (err) {
      console.error("Failed to load products", err);
      setProducts([]);
    } finally {
      setLoading(false);
    }
  };

  const categories = ["Electronics", "Furniture", "Clothing", "Books", "Sports"];

  return (
    <div className="home-page">
      {/* VIBRANT HERO SECTION */}
      <section className="hero-section">
        <div className="hero-content">
          <div className="hero-text">
            <h1 className="hero-title">Welcome to Flowtoo</h1>
            <p className="hero-subtitle">Discover amazing products at unbeatable prices</p>
            <div className="hero-cta">
              <button className="btn-primary btn-lg">Shop Now</button>
              <button className="btn-outline btn-lg">Learn More</button>
            </div>
          </div>
          <div className="hero-visual">
            <div className="floating-box box-1"></div>
            <div className="floating-box box-2"></div>
            <div className="floating-box box-3"></div>
            {/* FIXED HEART ICON */}
            <div
              className="heart-icon"
              onClick={() => console.log("Wishlisted!")}
              style={{ cursor: "pointer" }}
            >
              <FaHeart size={24} color="red" />
            </div>
          </div>
        </div>
      </section>

      {/* FEATURED PRODUCTS BANNER */}
      <section className="featured-banner">
        <div className="banner-content">
          <h2>üéâ Featured Deals</h2>
          <p>Limited time offers on our best-selling products</p>
          <button className="btn-secondary">View All Deals <FaArrowRight /></button>
        </div>
      </section>

      {/* SEARCH & FILTER SECTION */}
      <section className="search-filter-section">
        <div className="container">
          <div className="search-bar">
            <FaSearch className="search-icon" />
            <input
              type="text"
              placeholder="Search for products..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="search-input"
            />
          </div>

          <div className="filters">
            <div className="filter-group">
              <label>Category</label>
              <select value={category} onChange={(e) => setCategory(e.target.value)}>
                <option value="">All Categories</option>
                {categories.map((cat) => (
                  <option key={cat} value={cat}>
                    {cat}
                  </option>
                ))}
              </select>
            </div>

            <div className="filter-group">
              <label>Min Price (R)</label>
              <input
                type="number"
                placeholder="0"
                value={minPrice}
                onChange={(e) => setMinPrice(e.target.value)}
              />
            </div>

            <div className="filter-group">
              <label>Max Price (R)</label>
              <input
                type="number"
                placeholder="10000"
                value={maxPrice}
                onChange={(e) => setMaxPrice(e.target.value)}
              />
            </div>

            <button className="btn-filter" onClick={() => {
              setSearch("");
              setCategory("");
              setMinPrice("");
              setMaxPrice("");
            }}>
              <FaFilter /> Reset Filters
            </button>
          </div>
        </div>
      </section>

      {/* PRODUCTS GRID */}
      <section className="products-section">
        <div className="container">
          <h2 className="section-title">Our Products</h2>

          {loading ? (
            <Loading />
          ) : products.length === 0 ? (
            <div className="no-products">
              <p>No products found. Try adjusting your filters.</p>
            </div>
          ) : (
            <div className="products-grid">
              {products.map((product) => (
                <div key={product._id} className="product-card">
                  <div className="product-image-container">
                    <img
                      src={product.image}
                      alt={product.name}
                      className="product-image"
                    />
                    <div className="product-overlay">
                      <Link to={`/product/${product._id}`} className="btn-view">
                        <FaShoppingCart /> View Details
                      </Link>
                    </div>
                    <div className="product-wishlist">
                      <WishlistButton productId={product._id} />
                    </div>
                  </div>

                  <div className="product-info">
                    <h3 className="product-name">{product.name}</h3>

                    <div className="product-rating">
                      <div className="stars">
                        {[...Array(5)].map((_, i) => (
                          <FaStar
                            key={i}
                            className={`star ${i < Math.round(product.rating || 0) ? "filled" : ""}`}
                          />
                        ))}
                      </div>
                      <span className="rating-text">
                        {product.rating || 0} ({product.numReviews || 0})
                      </span>
                    </div>

                    <div className="product-price">
                      <span className="price">R{product.price?.toFixed(2) || "0.00"}</span>
                      <span className="badge">In Stock</span>
                    </div>

                    <button 
                      className="btn-add-cart"
                      onClick={() => addToCart(product)}
                    >
                      <FaShoppingCart /> Add to Cart
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>

