import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaBars, FaTimes, FaSearch, FaShoppingCart, FaUser } from "react-icons/fa";
import { useCart } from "../context/CartContext";
import "./Navbar.css";

export default function Navbar() {
  const [mobileOpen, setMobileOpen] = useState(false);
  const navigate = useNavigate();

  const cartContext = useCart() || {};
  const cartItems = Array.isArray(cartContext.cartItems) ? cartContext.cartItems : Array.isArray(cartContext.cart) ? cartContext.cart : [];
  const cartCount = cartItems.reduce((acc, item) => acc + (Number(item.qty || item.quantity || 0)), 0);

  const user = localStorage.getItem("user") ? JSON.parse(localStorage.getItem("user")) : null;

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setMobileOpen(false);
    navigate("/login");
  };

  const closeMobileMenu = () => setMobileOpen(false);

  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          {/* LEFT: HAMBURGER + FLOWTOO — ALIGNED FAR LEFT */}
          <div className="navbar-left">
            <div className="menu-toggle" onClick={() => setMobileOpen(!mobileOpen)}>
              {mobileOpen ? <FaTimes size={26} /> : <FaBars size={26} />}
            </div>
            <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
              Flowtoo
            </Link>
          </div>

          {/* RIGHT: ICONS — ALIGNED FAR RIGHT */}
          <div className="navbar-right">
            <FaSearch size={22} className="nav-icon" />
            <Link to="/cart" className="nav-icon cart-icon" onClick={closeMobileMenu}>
              <FaShoppingCart size={24} />
              {cartCount > 0 && <span className="cart-badge">{cartCount}</span>}
            </Link>
            {user ? (
              <div className="nav-icon" onClick={handleLogout}>
                <FaUser size={22} />
              </div>
            ) : (
              <Link to="/login" className="nav-icon" onClick={closeMobileMenu}>
                <FaUser size={22} />
              </Link>
            )}
          </div>
        </div>
      </nav>

      {/* MOBILE MENU */}
      <div className={`mobile-menu ${mobileOpen ? "open" : ""}`}>
        <Link to="/" onClick={closeMobileMenu}>Home</Link>
        <Link to="/cart" onClick={closeMobileMenu}>Cart ({cartCount})</Link>
        {user ? (
          <>
            <Link to="/myorders" onClick={closeMobileMenu}>My Orders</Link>
            <div onClick={handleLogout}>Logout</div>
          </>
        ) : (
          <>
            <Link to="/login" onClick={closeMobileMenu}>Login</Link>
            <Link to="/register" onClick={closeMobileMenu}>Sign Up</Link>
          </>
        )}
      </div>

      {mobileOpen && <div className="mobile-overlay" onClick={closeMobileMenu}></div>}
    </>
  );
}
