import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaBars, FaTimes, FaSearch, FaShoppingCart, FaUser } from "react-icons/fa";
import { useCart } from "../context/CartContext";
import "./Navbar.css";

export default function Navbar() {
  const [mobileOpen, setMobileOpen] = useState(false);
  const navigate = useNavigate();
  const { cartItems = [] } = useCart() || {};
  const cartCount = cartItems.reduce((acc, item) => acc + (item.qty || item.quantity || 0), 0);

  const user = localStorage.getItem("user") ? JSON.parse(localStorage.getItem("user")) : null;

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          {/* LEFT: MENU + LOGO */}
          <div className="navbar-left">
            <div className="menu-toggle" onClick={() => setMobileOpen(!mobileOpen)}>
              {mobileOpen ? <FaTimes size={24} /> : <FaBars size={24} />}
            </div>
            <Link to="/" className="navbar-logo">
              Flowtoo
            </Link>
          </div>

          {/* RIGHT: ICONS (Search, Cart, Profile) */}
          <div className="navbar-right">
            <div className="nav-icon search-icon" onClick={() => navigate("/search")}>
              <FaSearch size={22} />
            </div>
            <Link to="/cart" className="nav-icon cart-icon">
              <FaShoppingCart size={24} />
              {cartCount > 0 && <span className="cart-badge">{cartCount}</span>}
            </Link>
            {user ? (
              <div className="nav-icon" onClick={handleLogout}>
                <FaUser size={22} />
              </div>
            ) : (
              <Link to="/login" className="nav-icon">
                <FaUser size={22} />
              </Link>
            )}
          </div>
        </div>
      </nav>

      {/* MOBILE MENU */}
      {mobileOpen && (
        <div className="mobile-menu">
          <Link to="/" onClick={() => setMobileOpen(false)}>Home</Link>
          <Link to="/cart" onClick={() => setMobileOpen(false)}>Cart ({cartCount})</Link>
          {user ? (
            <>
              <Link to="/myorders" onClick={() => setMobileOpen(false)}>My Orders</Link>
              <div onClick={handleLogout}>Logout</div>
            </>
          ) : (
            <>
              <Link to="/login" onClick={() => setMobileOpen(false)}>Login</Link>
              <Link to="/register" onClick={() => setMobileOpen(false)}>Sign Up</Link>
            </>
          )}
        </div>
      )}
    </>
  );
}
