// src/pages/CartPage.jsx
import React from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../context/CartContext";
import { FaTrash, FaPlus, FaMinus } from "react-icons/fa";
import "./CartPage.css";

export default function CartPage() {
  const navigate = useNavigate();
  const { cartItems, addToCart, removeFromCart, clearCart } = useCart();

  // Calculate totals
  const subtotal = cartItems.reduce((acc, item) => {
    const price = Number(item.price) || 0;
    const qty = Number(item.qty || item.quantity || 1);
    return acc + price * qty;
  }, 0);

  const shipping = subtotal > 500 ? 0 : 79;
  const total = subtotal + shipping;

  if (cartItems.length === 0) {
    return (
      <div className="cart-empty">
        <h2>Your cart is empty</h2>
        <button onClick={() => navigate("/")} className="continue-shopping-btn">
          Continue Shopping
        </button>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h1>Your Cart</h1>

      <div className="cart-wrapper">
        {/* CART ITEMS */}
        <div className="cart-items">
          {cartItems.map((item) => (
            <div key={item._id} className="cart-item">
              <img src={item.image} alt={item.name} className="cart-item-image" />

              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <p className="cart-item-price">R{Number(item.price).toFixed(2)}</p>

                <div className="quantity-controls">
                  <button onClick={() => removeFromCart(item._id)}>
                    <FaMinus />
                  </button>
                  <span className="quantity">{item.qty || item.quantity}</span>
                  <button onClick={() => addToCart(item)}>
                    <FaPlus />
                  </button>
                </div>
              </div>

              <div className="cart-item-total">
                <p>R{(Number(item.price) * Number(item.qty || item.quantity)).toFixed(2)}</p>
                <button
                  className="remove-item-btn"
                  onClick={() => removeFromCart(item._id, true)}
                >
                  <FaTrash />
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* CART SUMMARY */}
        <div className="cart-summary">
          <h2>Order Summary</h2>

          <div className="summary-row">
            <span>Subtotal</span>
            <span>R{subtotal.toFixed(2)}</span>
          </div>

          <div className="summary-row">
            <span>Shipping</span>
            <span>{shipping === 0 ? "FREE" : `R${shipping.toFixed(2)}`}</span>
          </div>

          {shipping === 0 && (
            <div className="free-shipping-note">
              Free shipping applied!
            </div>
          )}

          <div className="summary-total">
            <strong>Total</strong>
            <strong>R{total.toFixed(2)}</strong>
          </div>

          {/* FIXED BUTTON â€” GOES TO CHECKOUT */}
          <button
            onClick={() => navigate("/checkout")}
            className="checkout-btn"
            disabled={cartItems.length === 0}
          >
            Proceed to Checkout
          </button>

          <button onClick={() => navigate("/")} className="continue-shopping">
            Continue Shopping
          </button>
        </div>
      </div>
    </div>
  );
}
