// src/components/Navbar.jsx
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaShoppingCart, FaUser, FaBars, FaBoxOpen, FaHome, FaTshirt, FaLaptop, FaCouch, FaTimes, FaSignOutAlt, FaUserCog } from "react-icons/fa";
import { useCart } from "../context/CartContext";
import "./Navbar.css";

export default function Navbar() {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const { cartItems } = useCart();
  const navigate = useNavigate();
  const profile = localStorage.getItem("profile");
  const user = profile ? JSON.parse(profile) : null;

  const cartCount = cartItems.reduce((total, item) => total + item.qty, 0);

  const handleLogout = () => {
    localStorage.removeItem("profile");
    setMobileMenuOpen(false);
    navigate("/login");
  };

  const closeMenu = () => setMobileMenuOpen(false);

  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          {/* Hamburger */}
          <div className="menu-icon" onClick={() => setMobileMenuOpen(!mobileMenuOpen)}>
            {mobileMenuOpen ? <FaTimes size={26} /> : <FaBars size={26} />}
          </div>

          {/* Logo */}
          <Link to="/" className="navbar-logo" onClick={closeMenu}>
            <span className="logo-text">Flowtoo</span>
          </Link>

          {/* Desktop Icons */}
          <div className="navbar-icons desktop-only">
            {user && (
              <Link to="/myorders" className="nav-icon">
                <FaBoxOpen size={22} />
              </Link>
            )}
            {user ? (
              <div className="nav-icon" onClick={handleLogout}>
                <FaSignOutAlt size={22} />
              </div>
            ) : (
              <Link to="/login" className="nav-icon">
                <FaUser size={22} />
              </Link>
            )}
            <Link to="/cart" className="nav-icon cart-icon">
              <FaShoppingCart size={24} />
              {cartCount > 0 && <span className="cart-badge">{cartCount}</span>}
            </Link>
          </div>
        </div>
      </nav>

      {/* MOBILE MENU â€” SLIDES IN FROM LEFT */}
      <div className={`mobile-menu ${mobileMenuOpen ? "open" : ""}`}>
        <div className="mobile-menu-header">
          <span>Hello, {user ? user.name || user.email : "Guest"}</span>
        </div>

        <div className="mobile-menu-links">
          <Link to="/" onClick={closeMenu} className="mobile-link">
            <FaHome /> Home
          </Link>

          <Link to="/cart" onClick={closeMenu} className="mobile-link">
            <FaShoppingCart /> Cart {cartCount > 0 && `(${cartCount})`}
          </Link>

          {user && (
            <>
              <Link to="/myorders" onClick={closeMenu} className="mobile-link">
                <FaBoxOpen /> My Orders
              </Link>

              <Link to="/profile" onClick={closeMenu} className="mobile-link">
                <FaUserCog /> Profile
              </Link>

              {user.isAdmin && (
                <Link to="/admin" onClick={closeMenu} className="mobile-link admin">
                  Admin Dashboard
                </Link>
              )}

              <div className="mobile-link logout" onClick={handleLogout}>
                <FaSignOutAlt /> Logout
              </div>
            </>
          )}

          {!user && (
            <>
              <Link to="/login" onClick={closeMenu} className="mobile-link">
                <FaUser /> Login
              </Link>
              <Link to="/register" onClick={closeMenu} className="mobile-link">
                Register
              </Link>
            </>
          )}

          <div className="mobile-categories">
            <h4>Shop by Category</h4>
            <Link to="/?category=Electronics" onClick={closeMenu} className="mobile-cat">
              <FaLaptop /> Electronics
            </Link>
            <Link to="/?category=Furniture" onClick={closeMenu} className="mobile-cat">
              <FaCouch /> Furniture
            </Link>
            <Link to="/?category=Clothing" onClick={closeMenu} className="mobile-cat">
              <FaTshirt /> Clothing
            </Link>
          </div>
        </div>
      </div>

      {/* Overlay when menu open */}
      {mobileMenuOpen && <div className="mobile-overlay" onClick={closeMenu}></div>}
    </>
  );
}
